import React from "react";
import { connect } from "react-redux";
import styled from "styled-components";

import { fetchPosts } from "../actions";
import {
    IPost,
    PostsListPageType,
    categoryType,
    PostPayload,
    PostState,
} from "../AppTypes";
import Post from "../components/Post";
import Loader from "../components/Loader";
import Message from "../components/Message";

export const PostsListStyle = styled.ul`
    padding: 15px;
    padding-top: 0px;
    list-style-type: none;
    z-index: 10;
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(400px, 1fr));
    grid-gap: 10px;
`;

class PostsListPage extends React.Component<PostsListPageType> {
    /* state = {
        isloaded: false,
        isError: false,
        headerMessageText: "",
        messageText: "",
        showMessage: false,
        categoryMessage: "info" as categoryType,
    };*/

    componentDidMount() {
        this.props.fetchPosts();
        console.log(this.props.postsReducer);
    }

    componentDidUpdate() {
        console.log(this.props.postsReducer);
        // console.log(this.props.postsReducer.allList);
        // console.log(this.props.postsReducer.allList.error);

        /*
        if (
            this.props.postsReducer.allList.error &&
            !this.props.postsReducer.allList.isLoading
        ) {
            /*
            this.setState({
                isloaded: true,
                isError: true,
                headerMessageText: "We're sorry we can't show you Blog!",
                messageText:
                    "An error occurred while loading data: " +
                    this.props.postsReducer.allList.data.text,
                showMessage: true,
                categoryMessage: "negative",
            }); 

        } else {
            console.log(
                "IsLoading: " + !this.props.postsReducer.allList.isLoading
            );
            if (
                !this.props.postsReducer.allList.isLoading &&
                this.props.postsReducer.allList.data.status
            ) {
                this.setState({
                    isloaded: true,
                });
            }
        }
        */
    }

    renderList() {
        if (!this.props.postsReducer.allList.data) {
            return <></>;
        }

        const ListOfPosts = this.props.postsReducer.allList.data.posts.map(
            (post: IPost) => {
                return <Post key={post["id"]} {...post} />;
            }
        );

        return (
            <>
                <PostsListStyle>{ListOfPosts}</PostsListStyle>
            </>
        );
    }

    render() {
        return <></>;

        return (
            <>
                {/*
                <Loader isActive={!this.state.isloaded} />
                <Message
                        showMessage={this.state.showMessage}
                        category={this.state.categoryMessage}
                        headerText={this.state.headerMessageText}
                        text={this.state.messageText}
                        color="red"
                        size="large"
        />*/}

                <div className="ui relaxed divided list">
                    {this.renderList()}
                </div>
            </>
        );
    }
}

const mapStateToProps = (state: { postsReducer: PostState }) => {
    return state;
};

export default connect(mapStateToProps, { fetchPosts })(PostsListPage);
