import React, { Component, ComponentType } from "react";
import styled from "styled-components";

export const LeftPanelStyle = styled.div`
    text-align: left;
    background-color: #424242;
    position: relative;
    width: 20%;
    min-height: 100vh;
    height: 100%;
    float: left;
`;
type linkType = {
    href: string;
    className: string;
    children: string;
};

const Link: ComponentType<linkType> = ({ href, className, children }) => (
    <a href={href} className={className}>
        {children}
    </a>
);

const StyledLink = styled(Link)`
    display: block;
    color: white;
    font-weight: bold;
    width: 80%;
    border: 1px solid #9e9e9e;
    margin: 5px;
`;

interface Props {}

interface State {}

class LeftPanel extends Component<Props, State> {
    state = {
        isActive: true,
    };

    render() {
        return (
            <LeftPanelStyle>
                <StyledLink href="/#" className="menu-link">
                    Styled, exciting Link 1
                </StyledLink>
                <StyledLink href="/#" className="menu-link">
                    Styled, exciting Link 2
                </StyledLink>
                <StyledLink href="/#" className="menu-link">
                    Styled, exciting Link 3
                </StyledLink>
                <StyledLink href="/#" className="menu-link">
                    Styled, exciting Link 4
                </StyledLink>
                <StyledLink href="/#" className="menu-link">
                    Styled, exciting Link 5
                </StyledLink>
            </LeftPanelStyle>
        );
    }
}

export default LeftPanel;
