import React, {ComponentType} from 'react';

enum categoryType {
  "info",
  "positive", 
  "negative",
  "warning"
};

enum sizeType {
  "mini",
  "tiny", 
  "small",
  "large",
  "big", 
  "huge",
  "massive",
};

type categoryTypeString = keyof typeof categoryType;
type sizeTypeString = keyof typeof sizeType;

type MessageProps = {
  visibility: boolean,
  category: categoryTypeString,
  headerText: String,
  text: String,
  color: String,
  size: sizeTypeString
}

const Message: ComponentType <MessageProps>  = ({ visibility, category, headerText, text, color, size }) => {
  const showMessage = visibility ? " visible " : " hidden ";
  const messageClass = "ui " + category +  " " + color +  showMessage + " message";

  const onCloseIconClick = () => {
    //TODO 
    console.log(this);
    //this.closest('.message').transition('fade');
  }

  return (
    <div className={messageClass}>
        <i 
          className="close icon" 
          onClick = {onCloseIconClick}
        />
        <div className="header">{headerText}</div>
        <p>{text}</p>
    </div>     
  );
};

export default Message;
