/* eslint-disable eqeqeq */
import React from "react";
import { connect } from "react-redux";
import styled from "styled-components";

import { fetchPosts } from "../actions";
import { IPost, PostState } from "../AppTypes";
import Post from "../components/Post";
import Loader from "../components/Loader";
import Message from "../components/Message";

export const PostsListStyle = styled.ul`
    padding: 15px;
    padding-top: 0px;
    list-style-type: none;
    z-index: 10;
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(400px, 1fr));
    grid-gap: 10px;
`;

class PostsListUsersPage extends React.Component<any> {
    state = {
        posts: [],
        isloaded: false,
    };

    componentDidMount() {
        this.props.fetchPosts();
    }

    renderMessage() {
        if (this.state.isloaded) {
            return (
                <Message
                    showMessage={
                        this.props.postsReducer.allPostsList.data.showMessage
                    }
                    category={
                        this.props.postsReducer.allPostsList.data
                            .categoryMessage
                    }
                    headerText={
                        this.props.postsReducer.allPostsList.data
                            .headerMessageText
                    }
                    text={this.props.postsReducer.allPostsList.data.messageText}
                    color="red"
                    size="large"
                />
            );
        }
    }

    renderList() {
        if (this.state.posts === []) {
            return <></>;
        }

        const ListOfPosts = this.state.posts.map((post: IPost) => {
            return <Post key={post["id"]} {...post} />;
        });

        return (
            <>
                <PostsListStyle>{ListOfPosts}</PostsListStyle>
            </>
        );
    }

    render() {
        return (
            <>
                <div className="ui relaxed divided list">
                    {this.renderList()}
                </div>
            </>
        );
    }
}

const mapStateToProps = (state: { postsReducer: PostState }) => {
    return state;
};

export default connect(mapStateToProps, { fetchPosts })(PostsListUsersPage);
