import {
    GET_POSTS_REQUEST,
    GET_POSTS_SUCCESS,
    GET_POSTS_FAIL,
} from "../actions/actionsTypes";

const postsReducer = (
    state = {
        isLoading: null,
        error: null,
        data: null,
    },
    action
) => {
    switch (action.type) {
        case GET_POSTS_REQUEST:
            return {
                ...state,
                allPostsList: {
                    isLoading: true,
                    error: null,
                    data: null,
                },
            };
        case GET_POSTS_SUCCESS:
            return {
                ...state,
                allPostsList: {
                    isLoading: false,
                    error: false,
                    data: action.payload,
                },
            };
        case GET_POSTS_FAIL:
            return {
                ...state,
                allPostsList: {
                    isLoading: false,
                    error: true,
                    data: action.payload,
                },
            };
        default:
            return state;
    }
};

export default postsReducer;
