import React from "react";
import { connect } from "react-redux";
import { fetchUserById } from "../actions";
import { IUser, UserComponentType, UserState } from "../AppTypes";

class User extends React.Component<UserComponentType, {}> {
    constructor(props: UserComponentType) {
        super(props);
    }

    state = {
        user: {},
        isloaded: false,
    };

    componentDidMount() {
        fetchUserById(this.props.userId);
    }

    componentDidUpdate() {
        if (
            !this.props.postsReducer.allPostsList.error &&
            !this.props.postsReducer.allPostsList.isLoading &&
            this.props.postsReducer.allPostsList.data != null &&
            !this.state.isloaded
        ) {
            this.setState({
                posts: this.props.postsReducer.allPostsList.data.posts,
                isloaded: true,
            });
        } else {
            if (
                this.props.postsReducer.allPostsList.error &&
                !this.state.isloaded
            ) {
                this.setState({
                    isloaded: true,
                });
            }
        }
    }

    render() {
        console.log(this.props);
        return <span>User Name: {this.props.userId}</span>;
    }
}

const mapStateToProps = (state: { userReducer: UserState }) => {
    return state;
};

export default connect(mapStateToProps, { fetchUserById })(User);
