import {
    GET_USERS_BY_ID_REQUEST,
    GET_USERS_BY_ID_SUCCESS,
    GET_USERS_BY_ID_FAIL,
} from "../actions/actionsTypes";
import { PostUserAction } from "../AppTypes";

export default (state = [], action: PostUserAction) => {
    //console.log("usersReducer - state:");
    //console.log(state);
    //console.log("usersReducer - action.payload:");
    //console.log(action.payload);

    switch (action.type) {
        case GET_USERS_BY_ID_REQUEST:
            return [
                ...state,
                {
                    userById: {
                        isLoading: true,
                        error: true,
                        data: true,
                    },
                },
            ];
        case GET_USERS_BY_ID_SUCCESS:
            return [
                ...state,
                {
                    userById: {
                        isLoading: false,
                        error: false,
                        data: action.payload,
                    },
                },
            ];
        default:
            return state;
    }
};
