import {
    GET_POSTS_REQUEST,
    GET_POSTS_SUCCESS,
    GET_POSTS_FAIL,
} from "../actions/actionsTypes";

export default (state = {}, action) => {
    switch (action.type) {
        //case "FETCH_POSTS":
        //    return action.payload;
        /*case GET_POSTS_SUCCESS:
            return {
                ...state,
                allPostsList: {
                    isLoading: false,
                    error: false,
                    data: action.payload,
                },
            };*/
        case GET_POSTS_SUCCESS:
            return {
                ...state,
                allList: { data: action.payload },
            };
        default:
            return state;
    }
};
