import {
    GET_USERS_BY_ID_REQUEST,
    GET_USERS_BY_ID_SUCCESS,
    GET_USERS_BY_ID_FAIL,
} from "../actions/actionsTypes";
import { PostUserAction } from "../AppTypes";

export default (
    state = [
        {
            userById: {
                isLoading: false,
                error: false,
                data: [],
            },
        },
    ],
    action: PostUserAction
) => {
    console.log("usersReducer - state:");
    console.log(state);

    switch (action.type) {
        case "FETCH_USER":
            return [...state, action.payload];
        case GET_USERS_BY_ID_SUCCESS:
            return {
                ...state,
                userById: {
                    isLoading: false,
                    error: false,
                    data: action.payload,
                },
            };
        default:
            return state;
    }
};
