import React from "react";
import UserHeader from "./UserHeader";
import styled from "styled-components";

const addComment = () => {
    alert("Dodawanie komenarzy już wkrótce!");
};

const PostElementStyle = styled.section`
    margin: 10px;
    padding: 0px;
    border: #dcedc8 solid 1px;
    background-color: #f1f8e9;
`;

const PostElementContentStyle = styled.section`
    padding: 5px;
`;

const PostElementFooterStyle = styled.section`
    display: block;
    margin: 0px;
    background-color: #f5f5f5;
    border-top: #dcedc8 solid 1px;
    min-height: 10vh;
`;

export const PostAuthor = styled.h3`
    text-align: left;
    padding: 5px;
`;

export const PostTitle = styled.h4`
    text-align: left;
    padding: 5px;
    margin: 0px;
    background-color: #dcedc8;
    padding: 5px;
    font-weight: bold;
`;

const Post = ({ post }) => {
    return (
        <div className="item" key={post.id}>
            <i className="large middle aligned icon user" />
            <div className="content">
                <div className="description">
                    <h2>{post.title}</h2>
                    <p>{post.body}</p>
                </div>
                <UserHeader userId={post.userId} />
            </div>
        </div>
    );
};

export default Post;
