import React , {Component} from "react";
import Post from "./components/Post";
import Loader from "./components/Loader";
import Message from "./components/Message";

import "./App.css";

class App extends Component{
  state = {
      posts:[],
      isloaded: false,
      isError: false
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then(
        response => {
          if (response.status === 200) {
            return response.json();
          } else {
            const headerText = "We're sorry we can't show you Blog!";
            const text = "An error occurred while loading data: <br/>" + response.text;

            return [];
          }
        }
      )
      .then(json => {
        this.setState({
            posts: json, 
            isloaded: true
        });
      });
  }

  render(){
    const postsList = this.state.posts.map((post) => {
      return <Post key={post['id']}  post={post} />;
    });

    return (
      <div className="App">
          <header className="App-header">
              <h1>Blog Beaty</h1>
          </header>
          <div>
              <Loader isActive={!this.state.isloaded}/>
              <Message />
              <ul className="post-list">{postsList}</ul>

          </div>
      </div>
    );
  }
}

export default App;
