/* eslint-disable eqeqeq */
import React from "react";
import { connect } from "react-redux";
import styled from "styled-components";

import { fetchPosts } from "../actions";
import {
    IPost,
    PostsListPageType,
    categoryType,
    PostPayload,
    PostState,
} from "../AppTypes";
import Post from "../components/Post";
import Loader from "../components/Loader";
import Message from "../components/Message";

export const PostsListStyle = styled.ul`
    padding: 15px;
    padding-top: 0px;
    list-style-type: none;
    z-index: 10;
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(400px, 1fr));
    grid-gap: 10px;
`;

class PostsListPage extends React.Component<PostsListPageType> {
    /* state = {
        isloaded: false,
        isError: false,
        headerMessageText: "",
        messageText: "",
        showMessage: false,
        categoryMessage: "info" as categoryType,
    };*/

    state = {
        posts: [],
        isloaded: false,
    };

    ListOfPosts = this.state.posts.map((post: IPost) => {
        return <Post key={post["id"]} {...post} />;
    });

    componentDidMount() {
        this.props.fetchPosts();
        console.log(
            "______________________ componentDIDMOUNT __________________________"
        );

        console.log(this.props.postsReducer.isLoading);
        console.log(
            "______________________ isLoading __________________________"
        );
        console.log(this.props.postsReducer.isLoading);
        console.log("______________________ error __________________________");
        console.log(this.props.postsReducer.error);
    }

    componentDidUpdate() {
        console.log(
            "______________________ componentDIDUPDATE __________________________"
        );
        console.log(this.props.postsReducer);
        console.log("______________________ error __________________________");
        console.log(this.props.postsReducer.allList.error);
        console.log(
            "______________________ isLoading __________________________"
        );
        console.log(this.props.postsReducer.allList.isLoading);
        console.log(
            "______________________ isLoaded __________________________"
        );
        console.log(this.state.isloaded);

        // console.log(this.props.postsReducer.allList);
        // console.log(this.props.postsReducer.allList.error);

        if (
            !this.props.postsReducer.allList.error &&
            !this.props.postsReducer.allList.isLoading &&
            this.props.postsReducer.allList.data != null &&
            !this.state.isloaded
        ) {
            this.setState({
                posts: this.props.postsReducer.allList.data.posts,
                isloaded: true,
            });
        }
        /*
            this.setState({
                isloaded: true,
                isError: true,
                headerMessageText: "We're sorry we can't show you Blog!",
                messageText:
                    "An error occurred while loading data: " +
                    this.props.postsReducer.allList.data.text,
                showMessage: true,
                categoryMessage: "negative",
            }); 

        } else {
            console.log(
                "IsLoading: " + !this.props.postsReducer.allList.isLoading
            );
            if (
                !this.props.postsReducer.allList.isLoading &&
                this.props.postsReducer.allList.data.status
            ) {
                this.setState({
                    isloaded: true,
                });
            }
        }
        */
    }

    renderList() {
        if (this.state.posts === []) {
            return <></>;
        }

        return (
            <>
                <PostsListStyle>{this.ListOfPosts}</PostsListStyle>
            </>
        );
    }

    render() {
        return (
            <>
                {/*
                <Loader isActive={!this.state.isloaded} />
                <Message
                        showMessage={this.state.showMessage}
                        category={this.state.categoryMessage}
                        headerText={this.state.headerMessageText}
                        text={this.state.messageText}
                        color="red"
                        size="large"
        />*/}

                <div className="ui relaxed divided list">
                    {this.renderList()}
                </div>
            </>
        );
    }
}

const mapStateToProps = (state: { postsReducer: PostState }) => {
    return state;
};

export default connect(mapStateToProps, { fetchPosts })(PostsListPage);
