import axios from "axios";

const createBaseApiConnection: any = () => {
    return axios.create({
        baseURL: "https://jsonplaceholder.typicode.com",
    });
};

export const getPosts: any = async () => {
    return await createBaseApiConnection.get("/posts");
};

export default createBaseApiConnection;
