import {
    GET_POSTS_REQUEST,
    GET_POSTS_SUCCESS,
    GET_POSTS_FAIL,
} from "./actionsTypes";

import { getPosts } from "../apis/jsonPlaceholder";

import { DispatchType, PostAction, PostState } from "../AppTypes";

export const fetchPosts: any = () => async (dispatch: DispatchType) => {
    setTimeout(async function () {
        let payload: PostState;
        console.log("action: GET_POSTS_REQUEST");

        dispatch({
            type: GET_POSTS_REQUEST,
            payload: {
                posts: [],
                status: 0,
                text: "",
                error: false,
            },
        });

        try {
            let isError = true;
            const response = await getPosts();

            if (response.status === 200) {
                isError = false;
            }
            //const responseJson = await response.json();

            payload = {
                posts: response.data,
                status: response.status,
                text: response.statusText,
                error: isError,
            };

            dispatch({ type: GET_POSTS_SUCCESS, payload: payload });
        } catch (error: any) {
            payload = {
                posts: [],
                status: 404,
                text: error.toJSON().message,
                error: true,
            };

            dispatch({ type: GET_POSTS_FAIL, payload: payload });
        }
    }, 2000);
};

