import {
    FETCH_POSTS,
    GET_POSTS_REQUEST,
    GET_POSTS_SUCCESS,
    GET_POSTS_FAIL,
} from "../actions/actionsTypes";

const postsReducer = (
    state = {
        posts: [],
        status: undefined,
    },
    action
) => {
    switch (action.type) {
        case FETCH_POSTS:
            return action.payload;

        case GET_POSTS_REQUEST:
            return {
                ...state,
                byBulk: {
                    isLoading: true,
                    error: null,
                    data: null,
                },
            };
        case GET_POSTS_SUCCESS:
            return {
                ...state,
                byBulk: {
                    isLoading: false,
                    error: false,
                    data: action.payload,
                },
            };
        case GET_POSTS_FAIL:
            return {
                ...state,
                byBulk: {
                    isLoading: false,
                    error: action.payload,
                    data: false,
                },
            };
        default:
            return state;
    }
};

export default postsReducer;
