/* eslint-disable eqeqeq */
import React from "react";
import { connect } from "react-redux";
import styled from "styled-components";

import { fetchPosts } from "../actions";
import { IPost, PostsListPageType, categoryType, PostState } from "../AppTypes";
import Post from "../components/Post";
import Loader from "../components/Loader";
import Message from "../components/Message";

export const PostsListStyle = styled.ul`
    padding: 15px;
    padding-top: 0px;
    list-style-type: none;
    z-index: 10;
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(400px, 1fr));
    grid-gap: 10px;
`;

class PostsListPage extends React.Component<PostsListPageType> {
    state = {
        posts: [],
        isloaded: false,
    };

    componentDidMount() {
        this.props.fetchPosts();
        /*
        console.log("__________________ componentDIDMOUNT __________________");
        console.log(this.props.postsReducer.isLoading);
        console.log("__________________ isLoading ______________________");
        console.log(this.props.postsReducer.isLoading);
        console.log("______________________ error __________________________");
        console.log(this.props.postsReducer.error);
        */
    }

    componentDidUpdate() {
        console.log("________________ componentDIDUPDATE _________________");
        console.log(this.props.postsReducer);
        /*
        console.log("________________ componentDIDUPDATE _________________");
        console.log(this.props.postsReducer);
        console.log("______________________ error __________________________");
        console.log(this.props.postsReducer.allList.error);
        console.log("__________________ isLoading _____________________");
        console.log(this.props.postsReducer.allList.isLoading);
        console.log("__________________ isLoaded ________________________");
        console.log(this.state.isloaded);
        */

        if (
            !this.props.postsReducer.allList.error &&
            !this.props.postsReducer.allList.isLoading &&
            this.props.postsReducer.allList.data != null &&
            !this.state.isloaded
        ) {
            this.setState({
                posts: this.props.postsReducer.allList.data.posts,
                isloaded: true,
            });
        } else {
            if (this.props.postsReducer.allList.error && !this.state.isloaded) {
                this.setState({
                    //posts: [],
                    isloaded: true,
                });
            }
        }
    }

    renderList() {
        if (this.state.posts === []) {
            return <></>;
        }

        const ListOfPosts = this.state.posts.map((post: IPost) => {
            return <Post key={post["id"]} {...post} />;
        });

        return (
            <>
                <PostsListStyle>{ListOfPosts}</PostsListStyle>
            </>
        );
    }

    render() {
        return (
            <>
                <Loader isActive={!this.state.isloaded} />
                <Message
                    showMessage={this.state.showMessage}
                    category={this.state.categoryMessage}
                    headerText={this.state.headerMessageText}
                    text={this.state.messageText}
                    color="red"
                    size="large"
                />
                <div className="ui relaxed divided list">
                    {this.renderList()}
                </div>
            </>
        );
    }
}

const mapStateToProps = (state: { postsReducer: PostState }) => {
    return state;
};

export default connect(mapStateToProps, { fetchPosts })(PostsListPage);
