import {
    FETCH_POSTS,
    GET_POSTS_REQUEST,
    GET_POSTS_SUCCESS,
    GET_POSTS_FAIL,
} from "./actionsTypes";

import { getPosts } from "../apis/jsonPlaceholder";

import { DispatchType, PostAction } from "../AppTypes";

export const fetchPosts: any = () => async (dispatch: DispatchType) => {
    setTimeout(async function () {
        console.log("action: GET_POSTS_REQUEST");

        dispatch({
            type: GET_POSTS_REQUEST,
            payload: {
                posts: [],
                status: 0,
                text: "",
                error: false,
            },
        });

        try {
            const response = await getPosts();
            const responseJson = await response.json();
            dispatch({ type: GET_POSTS_SUCCESS, payload: responseJson });
        } catch (error) {
            dispatch({ type: GET_POSTS_FAIL, payload: error });
        }
    }, 2000);
};

