import {
    GET_POSTS_REQUEST,
    GET_POSTS_SUCCESS,
    GET_POSTS_FAIL,
} from "./actionsTypes";

import { getPosts, getUserById } from "../apis/jsonPlaceholder";
import { categoryType, DispatchType, PostPayload } from "../AppTypes";
import _ from "lodash";

export const fetchPostsFull: any = () => async (dispatch: DispatchType) => {
    setTimeout(async function () {
        let payload: PostPayload;

        dispatch({
            type: GET_POSTS_REQUEST,
            payload: {
                posts: [],
                status: 0,
                text: "",
                error: false,
                headerMessageText: "",
                messageText: "",
                showMessage: false,
                categoryMessage: "info" as categoryType,
            },
        });

        try {
            let isError = true;
            const response = await getPosts();

            if (response.status === 200) {
                isError = false;
            }

            payload = {
                posts: response.data,
                status: response.status,
                text: response.statusText,
                error: isError,
                headerMessageText: "",
                messageText: "",
                showMessage: false,
                categoryMessage: "info" as categoryType,
            };

            dispatch({ type: GET_POSTS_SUCCESS, payload: payload });
        } catch (error: any) {
            payload = {
                posts: [],
                status: 404,
                text: error.toJSON().message,
                error: true,
                headerMessageText: "We're sorry we can't show you posts!",
                messageText:
                    "An error occurred while loading data: " +
                    error.toJSON().message,
                showMessage: true,
                categoryMessage: "negative",
            };

            dispatch({ type: GET_POSTS_FAIL, payload: payload });
        }
    }, 1000);
};
export const fetchPosts = () => async (dispatch: any) => {
    const response = await getPosts();

    dispatch({ type: "FETCH_POSTS", payload: response.data });
};

export const fetchUser = (id: number) => async (dispatch: any) => {
    const response = await getUserById(id);

    dispatch({ type: "FETCH_USER", payload: response.data });
};
export const fetchPostsAndUsers =
    () => async (dispatch: any, getState: any) => {
        await dispatch(fetchPosts());

        _.chain(getState().posts)
            .map("userId")
            .uniq()
            .forEach((id) => dispatch(fetchUser(id)))
            .value();
    };
