import {
    FETCH_POSTS,
    GET_POSTS_REQUEST,
    GET_POSTS_SUCCESS,
    GET_POSTS_FAIL,
} from "../actions/actionsTypes";

const postsReducer = (state, action) => {
    switch (action.type) {
        case FETCH_POSTS:
            return action.payload;

        case GET_POSTS_REQUEST:
            return {
                ...state,
                allList: {
                    isLoading: true,
                    error: null,
                    data: null,
                },
            };
        case GET_POSTS_SUCCESS:
            return {
                ...state,
                allList: {
                    isLoading: false,
                    error: false,
                    data: action.payload,
                },
            };
        case GET_POSTS_FAIL:
            return {
                ...state,
                allList: {
                    isLoading: false,
                    error: action.payload,
                    data: false,
                },
            };
        default:
            return state;
    }
};

export default postsReducer;
