import React, {ComponentType} from 'react';

export enum categoryType {
  info,
  positive, 
  negative,
  warning
};

export enum sizeType {
  "mini",
  "tiny", 
  "small",
  "large",
  "big", 
  "huge",
  "massive",
};

type MessageProps = {
  visibility: boolean,
  category: categoryType,
  headerText: String,
  text: String,
  color: String,
  size: sizeType
}

const Message: ComponentType <MessageProps>  = ({ visibility, category, headerText, text, color }) => {
  const showMessage = visibility ? " visible " : " hidden ";
  const messageClass = "ui " + category +  " " + color +  showMessage + " message";

  return (
    <div className={messageClass}>
      <i className="close icon"></i>
        <div className="header">{headerText}</div>
        <p>{text}</p>
      </div>     
  );
};

export default Message;
