import React from "react";
import styled from "styled-components";
import { PostType } from "../App";

type PostProps = PostType;

export const PostElementFooterStyle = styled.div`
    display: block;
    margin: 0px;
    background-color: #f5f5f5;
    border-top: #dcedc8 solid 1px;
    min-height: 10vh;
`;

const Post: React.ComponentType<PostProps> = ({ userId, title, body }) => {
    return (
        <li className="post-element">
            <h4>Autor: {userId}</h4>
            <h3>
                <b>{title}</b>
            </h3>
            <br />
            <p>{body}</p>
            <span className="post-element-footer"></span>
        </li>
    );
};

export default Post;
