import axios from "axios";

const jsonPlaceholder: any = () => {
    return axios.create({
        baseURL: "https://jsonplaceholder.typicode.com",
    });
};

export const getPosts: any = async () => {
    return await jsonPlaceholder.get("/posts");
};

export default jsonPlaceholder;
