import React from "react";
import { connect } from "react-redux";
import { fetchPosts } from "../actions";
import { categoryType, IPost, PostsListPageType } from "../AppTypes";
import Post from "../components/Post";

class PostsListPage extends React.Component<PostsListPageType> {
    state = {
        isloaded: false,
        isError: false,
        headerMessageText: "",
        messageText: "",
        showMessage: false,
        categoryMessage: "info" as categoryType,
    };

    componentDidMount() {
        console.log(this.props);
        this.props.fetchPosts();
    }

    renderList() {
        const ListOfPosts = this.props.posts.map((post: IPost) => {
            return <Post key={post["id"]} {...post} />;
        });

        return (
            <>
                <ul className="post-list">{ListOfPosts}</ul>
            </>
        );
    }

    render() {
        return (
            <div className="ui relaxed divided list">{this.renderList()}</div>
        );
    }
}

const mapStateToProps = (state: { posts: IPost[]; status: number }) => {
    return {
        posts: state.posts,
        status: state.status,
    };
};

export default connect(mapStateToProps, { fetchPosts })(PostsListPage);
