import React, { ComponentType, useRef } from "react";
import styled from "styled-components";

const ButtonStyle = styled.button`
    background: ${(props) =>
        props.about === "commentPrimary" ? "#424242" : "white"};
    color: ${(props) =>
        props.about === "commentPrimary" ? "white" : "#424242"};
    font-size: 1em;
    margin: 1em;
    radius: 5px;
    padding: 0.25em 1em;
    border: 2px solid #bdbdbd;
    border-radius: 5px;
`;

type ButtonType = {
    id: string;
    name: string;
    about: "commentPrimary" | "commentHover";
    ref: string;
};

const Button: ComponentType<ButtonType> = ({ id, name, about, ref }) => {
    const buttonStyleRef = React.createRef();

    return (
        <ButtonStyle ref={buttonStyleRef} about={about} id={id}>
            {name}
        </ButtonStyle>
    );
};

export default Button;
